import{_ as e,c as l,ah as n,o as a}from"./chunks/framework.BWXOFiC5.js";const c=JSON.parse('{"title":"Preferences in your ImmyBot Instance","description":"","frontmatter":{},"headers":[],"relativePath":"Documentation/Administration/preferences.md","filePath":"Documentation/Administration/preferences.md"}'),i={name:"Documentation/Administration/preferences.md"};function s(d,t,r,o,f,y){return a(),l("div",null,t[0]||(t[0]=[n('<h1 id="preferences-in-your-immybot-instance" tabindex="-1">Preferences in your ImmyBot Instance <a class="header-anchor" href="#preferences-in-your-immybot-instance" aria-label="Permalink to &quot;Preferences in your ImmyBot Instance&quot;">​</a></h1><h2 id="overview-and-or-objective" tabindex="-1">Overview and or Objective <a class="header-anchor" href="#overview-and-or-objective" aria-label="Permalink to &quot;Overview and or Objective&quot;">​</a></h2><p>This document is intended to inform you on the preferences available in your ImmyBot instance.</p><h2 id="prerequisites" tabindex="-1">Prerequisites <a class="header-anchor" href="#prerequisites" aria-label="Permalink to &quot;Prerequisites&quot;">​</a></h2><p>An active ImmyBot subscription or <a href="https://www.immy.bot/pricing/" target="_blank" rel="noreferrer">trial</a></p><p>MSP Admin or Tenant Admin permissions on your User.</p><h2 id="instance-level-preferences" tabindex="-1">Instance Level Preferences <a class="header-anchor" href="#instance-level-preferences" aria-label="Permalink to &quot;Instance Level Preferences&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Category</th><th style="text-align:left;">Preference</th><th style="text-align:left;">Description</th><th style="text-align:left;">Interface Type</th></tr></thead><tbody><tr><td style="text-align:left;">Maintenance</td><td style="text-align:left;">Email BCC</td><td style="text-align:left;">Add people to be BCC&#39;d on every email that immy.bot produces.</td><td style="text-align:left;">Drop Down Selection</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Enable Session Emails</td><td style="text-align:left;">Disabling this preference will prevent all detection and post-maintenance emails from being sent to all tenants. (Enabled by default)</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Default Branding</td><td style="text-align:left;">Set the default branding that is used for emails.</td><td style="text-align:left;">Drop Down Selection</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Default Time Zone</td><td style="text-align:left;">Set the default timezone to be used on schedules and deployments.</td><td style="text-align:left;">Drop Down Selection</td></tr><tr><td style="text-align:left;">Remote Control</td><td style="text-align:left;">immy.bot Remote Control - Beta</td><td style="text-align:left;">Choose whether immy.bot users can remotely access devices using the built-in immy.bot remote control. Can be overridden in individual tenant preferences.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Require Consent For Remote Sessions</td><td style="text-align:left;">Enabling this will force an end-user to consent to a remote session before it can be established.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;">User</td><td style="text-align:left;">Allow Non-Admins and Non-MSP Users to Use Terminal and Edit Scripts</td><td style="text-align:left;">Disable this to prevent non-admins and non-MSP users from running scripts from computer terminals, and from managing scripts</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Allow Non-Admin Users to Manage Deployments</td><td style="text-align:left;">Disable this preference to prevent non-admin users from being able to create, update, and delete deployments.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Azure User Sync</td><td style="text-align:left;">Enabling this feature will schedule a sync to run every hour that updates persons with the latest Azure User data</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Enable Access Requests</td><td style="text-align:left;">Set whether your customers can request access to your instance.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Enable User Impersonation</td><td style="text-align:left;">Enabling this preference allows MSP admins to impersonate any user in the system</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">immy.bot Support Access</td><td style="text-align:left;">Granting access will allow immy.bot support technicians to log into your instance.</td><td style="text-align:left;">Approve / Revoke Button</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Require Change Requests for Non-Admin Cross-Tenant Deployments</td><td style="text-align:left;">Set whether you would like to require MSP non-admins to submit change requests when creating or updating cross-tenant deployments.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">User Affinity Sync</td><td style="text-align:left;">Enabling this feature will schedule a sync to run every 24 hours, logging the current user to determine who is using the computer. (Recommended)</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;">ImmyBot Updates</td><td style="text-align:left;">Automatic immy.bot Version Upgrades</td><td style="text-align:left;">Enabling this will automatically upgrade your instance to the latest version of immy.bot after the &quot;Days to wait after update is available&quot; has passed.</td><td style="text-align:left;">Drop down and numerical input</td></tr><tr><td style="text-align:left;">Script Editor</td><td style="text-align:left;">Disconnect least-active editor service when limit is reached</td><td style="text-align:left;">Enabling this will automatically disconnect the least-active editor service when the limit of active editor services is reached for this instance to service a new editor request.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;">Computers</td><td style="text-align:left;">Auto resolve new computers with recent OS install dates</td><td style="text-align:left;">Enabling this feature will treat all computers with recently installed OS (less than 24 hours old) as repurposed / wiped</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Enable historical computer inventory tracking</td><td style="text-align:left;">Enable this to keep all computer inventory script results instead of only the latest results.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Enable Non-Essential Device Inventorying</td><td style="text-align:left;">Enabling this feature will cause immy.bot to automatically schedule all device inventory jobs when inventory details become outdated. (Recommended)</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Onboarding</td><td style="text-align:left;">Enabling this feature will allow tenants to choose whether to automatically run maintenance against newly synced computers.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Stale Computer Age</td><td style="text-align:left;">This defines the number of days since an offline computer&#39;s last agent connection, after which the computer is considered &quot;stale.&quot;</td><td style="text-align:left;">Numerical input</td></tr><tr><td style="text-align:left;">Scripts</td><td style="text-align:left;">Default Script Timeouts</td><td style="text-align:left;">The default timeouts (in seconds) for scripts that are run by immy.bot.</td><td style="text-align:left;">Numerical input</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Script Path</td><td style="text-align:left;">Set the path here</td><td style="text-align:left;">Button</td></tr><tr><td style="text-align:left;">Integrations</td><td style="text-align:left;">Exclude Chocolatey From Software Search</td><td style="text-align:left;">Enabling this preference will exclude all chocolatey packages from the software search on the deployment page.</td><td style="text-align:left;">Button</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">immy.bot Chocolatey Feed</td><td style="text-align:left;">When enabled, Chocolatey requests are proxied through the immy.bot feed to bypass rate limiting. (Recommended) When disabled, requests go directly through Chocolatey and are subject to its rate limiting policy.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Ninite Integration</td><td style="text-align:left;">Enabling this integration provides a new software type, Ninite. If enabled, you must upload a copy of NiniteOne.exe under My Software. When installing software through Ninite, NiniteOne.exe will be added to the computer if it is not already present.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Enable Integration Audit Logging</td><td style="text-align:left;">Enables the audit tab on integrations.</td><td style="text-align:left;">Bool</td></tr></tbody></table><h2 id="tenant-level-preferences" tabindex="-1">Tenant Level Preferences <a class="header-anchor" href="#tenant-level-preferences" aria-label="Permalink to &quot;Tenant Level Preferences&quot;">​</a></h2><p>These can be managed in the individual tenant, or from Show More &gt; Preferences and then selecting the tenant from the drop down at the top.</p><table tabindex="0"><thead><tr><th style="text-align:left;">Category</th><th style="text-align:left;">Preference</th><th style="text-align:left;">Description</th><th style="text-align:left;">Interface Type</th></tr></thead><tbody><tr><td style="text-align:left;">Remote Control</td><td style="text-align:left;">immy.bot Remote Control Beta</td><td style="text-align:left;">Set whether you want to allow immy.bot remote control for devices in this tenant. You can also use the system&#39;s preference.</td><td style="text-align:left;">Radial Button</td></tr><tr><td style="text-align:left;">Remote Control</td><td style="text-align:left;">Require Consent For Remote Sessions</td><td style="text-align:left;">Enabling this will force an end-user to consent to a remote session before it can be established.</td><td style="text-align:left;">Radial Button</td></tr><tr><td style="text-align:left;">Computers</td><td style="text-align:left;">Auto resolve new computers with recent OS install dates</td><td style="text-align:left;">Enabling this feature will treat all computers with recently installed OS (less than 24 hours old) as repurposed / wiped. A computer already known to immy.bot is wiped clean and setup for a new person. The old reference to this computer will automatically be deleted.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Computers Excluded From Maintenance</td><td style="text-align:left;">Add/Remove computers that should be excluded from sessions. An excluded computer cannot have any software or tasks deployed to it. However, inventory will still be run on it.</td><td style="text-align:left;">Drop Down Selection</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Exclude From Cross-Tenant Deployments and Schedules</td><td style="text-align:left;">When enabled, this tenant and its computers will not be considered when resolving cross-tenant deployments and schedules.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Onboarding</td><td style="text-align:left;">Enabling this feature will allow newly synced computers to be onboarded.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Onboarding Patching</td><td style="text-align:left;">Enabling this feature will allow for patching during onboarding. This currently only works when the ConnectWise Automate integration is enabled.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;">Users</td><td style="text-align:left;">User Affinity Sync</td><td style="text-align:left;">Logs the current logged in user to determine who uses a computer. The System User Affinity Sync setting must be enabled for this to take effect.</td><td style="text-align:left;">Bool</td></tr><tr><td style="text-align:left;">Maintenance</td><td style="text-align:left;">Business Hours</td><td style="text-align:left;">Set the business hours.</td><td style="text-align:left;">Time Selection</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Default Time Zone</td><td style="text-align:left;">Set the timezone to be used for this tenant.</td><td style="text-align:left;">Drop Down Selection</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Email BCC</td><td style="text-align:left;">Add people to be BCC&#39;d on every email that immy.bot produces.</td><td style="text-align:left;">Selection</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Enable Session Emails</td><td style="text-align:left;">Disabling this preference will prevent all detection and post-maintenance emails from being sent. (Enabled by default)</td><td style="text-align:left;">Bool</td></tr></tbody></table><h2 id="user-preferences" tabindex="-1">User Preferences <a class="header-anchor" href="#user-preferences" aria-label="Permalink to &quot;User Preferences&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Category</th><th style="text-align:left;">Preference</th><th style="text-align:left;">Description</th><th style="text-align:left;">Interface Type</th></tr></thead><tbody><tr><td style="text-align:left;">Theme</td><td style="text-align:left;">Color Theme</td><td style="text-align:left;">Toggle between a light and dark color theme.</td><td style="text-align:left;">Toggle button</td></tr><tr><td style="text-align:left;">PII</td><td style="text-align:left;">PII Mask Mode</td><td style="text-align:left;">When enabled, all personally identifiable information (PII), such as tenant and user names, will be visually masked in the UI with asterisks.</td><td style="text-align:left;">Bool</td></tr></tbody></table><p><br><br><br></p><div class="note custom-block github-alert"><p class="custom-block-title">Document information</p><p>Author: Mark Gomez <br> Date Published: 09/19/2025 <br> Date Revised: N/A <br> Version Number: 1.0</p></div>',15)]))}const h=e(i,[["render",s]]);export{c as __pageData,h as default};
