import{_ as t,c as i,ah as o,o as n}from"./chunks/framework.BWXOFiC5.js";const p=JSON.parse('{"title":"Subscription Management and Licensing","description":"","frontmatter":{},"headers":[],"relativePath":"Documentation/Administration/subscription-managment.md","filePath":"Documentation/Administration/subscription-managment.md"}'),a={name:"Documentation/Administration/subscription-managment.md"};function r(s,e,c,u,l,m){return n(),i("div",null,e[0]||(e[0]=[o('<h1 id="subscription-management-and-licensing" tabindex="-1">Subscription Management and Licensing <a class="header-anchor" href="#subscription-management-and-licensing" aria-label="Permalink to &quot;Subscription Management and Licensing&quot;">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>This document will give you a high level overview of managing your ImmyBot Subscriptions and Licenses. You cannot manage instance resources on your own, you must open a ticket with support to take care of this for you.</p><div class="info custom-block"><p class="custom-block-title">ImmyBot support can assist</p><p>If you would like ImmyBot to make changes to your subscription, you must make the request in writing to customercare@immy.bot</p></div><h2 id="prerequisites" tabindex="-1">Prerequisites <a class="header-anchor" href="#prerequisites" aria-label="Permalink to &quot;Prerequisites&quot;">​</a></h2><ul><li>MSP Admin role on your user account.</li><li>We are assuming you&#39;re logged into ImmyBot.</li></ul><h2 id="subscription-management" tabindex="-1">Subscription Management <a class="header-anchor" href="#subscription-management" aria-label="Permalink to &quot;Subscription Management&quot;">​</a></h2><ol><li>Go to Billing tab</li></ol><ul><li>Show More &gt; Billing</li><li>From here you can view your current subscription and features</li></ul><ol start="2"><li>To edit your subscription <ul><li>Click on &quot;Click here to edit your subscription&quot;</li><li>If you&#39;re authorized to do so, you can move to ImmyBot Standard if you&#39;re a ImmyBot Starter client</li><li>You can also edit your ImmyBot agent count if you&#39;re an ImmyBot Standard client <ul><li>Click on the Standard Subscription &gt; Edit Subscription &gt; use the Drop down under device count to select what you want and then click &quot;Update Subscription&quot;</li><li>Updates are effective after next billing cycle. If you want the licenses sooner, email customercare@immy.bot and you will be prorated for the difference that month.</li></ul></li></ul></li></ol><h2 id="license-management" tabindex="-1">License Management <a class="header-anchor" href="#license-management" aria-label="Permalink to &quot;License Management&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">ImmyBot Standard Only</p><p>This does not apply to ImmyBot Starter and ImmyBot Forever clients</p></div><p>Licenses are only applied to computers that you&#39;ve ran maintenance against since the beginning of your billing period. You can edit the number of licenses you have by editing your subscription. You cannot move licenses around after they&#39;re assigned to an endpoint. If you have any questions about your licenses or subscription please contact customercare@immy.bot</p><p><br><br><br></p><div class="note custom-block github-alert"><p class="custom-block-title">Document information</p><p>Author: Mark Gomez <br> Date Published: 09/10/2025 <br> Date Revised: N/A <br> Version Number: 1.0</p></div>',15)]))}const b=t(a,[["render",r]]);export{p as __pageData,b as default};
